import React from 'react';
import arrWords from '../data/words.js';

const AnswerSection = () => {
  const currentWord = arrWords[randomNum];

  const randomNum = ~~(Math.random() * arrWords.length);

  return (
    <>
      <form>
        <input type='text' class='answer' placeholder='정답 입력' />
        <button class='submit-btn'>제출</button>
      </form>
      <div class='answer-box'>
        <h2 class='title-h2'>정답공간</h2>
        <ul class='answer-ul'></ul>
      </div>
    </>
  );
};

export default AnswerSection;
